axios.defaults.baseURL="http://admin-v2.test/api/";$("#changeLang").click(function(){$(this).prop("checked")?axios.get("/change_lang/tr").then(function(e){window.location.reload()}).catch(function(e){console.log(e)}):axios.get("/change_lang/en").then(function(e){window.location.reload()}).catch(function(e){console.log(e)})});$("#selectRole").change(function(){$(".permCheck").attr("checked",!1),this.value&&axios.get("/role_permissions/"+this.value).then(function(e){e.data.data.forEach(function(c){$("#perm_"+c.id).attr("checked",!0)})}).catch(function(e){console.log(e.data)})});$("#selectAll").click(function(){this.checked?$(".permCheck").attr("checked",!0):$(".permCheck").attr("checked",!1)});
